import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  insertPracticeSessionSchema,
  insertPracticeGoalSchema,
  insertChordProgressionSchema,
  insertPracticeScheduleSchema,
  insertPracticeHistorySchema,
  insertSongSchema,
  insertSongCollectionSchema,
  insertSongPracticeSessionSchema
} from "@shared/schema";
import { getUncachableSpotifyClient, convertSpotifyTrackToSong, getTrackAudioFeatures } from "./spotify";

export async function registerRoutes(app: Express): Promise<Server> {
  // Practice Sessions
  app.get("/api/practice-sessions/:userId", async (req, res) => {
    try {
      const sessions = await storage.getUserPracticeSessions(req.params.userId);
      res.json(sessions);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch practice sessions" });
    }
  });

  app.post("/api/practice-sessions", async (req, res) => {
    try {
      const validatedData = insertPracticeSessionSchema.parse(req.body);
      const session = await storage.createPracticeSession(validatedData);
      res.json(session);
    } catch (error) {
      res.status(400).json({ error: "Invalid practice session data" });
    }
  });

  app.patch("/api/practice-sessions/:id", async (req, res) => {
    try {
      const updated = await storage.updatePracticeSession(req.params.id, req.body);
      if (!updated) {
        return res.status(404).json({ error: "Practice session not found" });
      }
      res.json(updated);
    } catch (error) {
      res.status(500).json({ error: "Failed to update practice session" });
    }
  });

  // Practice Goals
  app.get("/api/practice-goals/:userId", async (req, res) => {
    try {
      const goals = await storage.getUserPracticeGoals(req.params.userId);
      res.json(goals);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch practice goals" });
    }
  });

  app.post("/api/practice-goals", async (req, res) => {
    try {
      const validatedData = insertPracticeGoalSchema.parse(req.body);
      const goal = await storage.createPracticeGoal(validatedData);
      res.json(goal);
    } catch (error) {
      res.status(400).json({ error: "Invalid practice goal data" });
    }
  });

  app.patch("/api/practice-goals/:id", async (req, res) => {
    try {
      const updated = await storage.updatePracticeGoal(req.params.id, req.body);
      if (!updated) {
        return res.status(404).json({ error: "Practice goal not found" });
      }
      res.json(updated);
    } catch (error) {
      res.status(500).json({ error: "Failed to update practice goal" });
    }
  });

  // Chord Progressions
  app.get("/api/chord-progressions/:userId", async (req, res) => {
    try {
      const progressions = await storage.getUserChordProgressions(req.params.userId);
      res.json(progressions);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch chord progressions" });
    }
  });

  app.post("/api/chord-progressions", async (req, res) => {
    try {
      const validatedData = insertChordProgressionSchema.parse(req.body);
      const progression = await storage.createChordProgression(validatedData);
      res.json(progression);
    } catch (error) {
      res.status(400).json({ error: "Invalid chord progression data" });
    }
  });

  app.delete("/api/chord-progressions/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteChordProgression(req.params.id);
      if (!deleted) {
        return res.status(404).json({ error: "Chord progression not found" });
      }
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: "Failed to delete chord progression" });
    }
  });

  // Practice Schedules
  app.get("/api/practice-schedules/:userId", async (req, res) => {
    try {
      const schedules = await storage.getUserPracticeSchedules(req.params.userId);
      res.json(schedules);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch practice schedules" });
    }
  });

  app.post("/api/practice-schedules", async (req, res) => {
    try {
      const validatedData = insertPracticeScheduleSchema.parse(req.body);
      const schedule = await storage.createPracticeSchedule(validatedData);
      res.json(schedule);
    } catch (error) {
      res.status(400).json({ error: "Invalid practice schedule data" });
    }
  });

  app.patch("/api/practice-schedules/:id", async (req, res) => {
    try {
      const updated = await storage.updatePracticeSchedule(req.params.id, req.body);
      if (!updated) {
        return res.status(404).json({ error: "Practice schedule not found" });
      }
      res.json(updated);
    } catch (error) {
      res.status(500).json({ error: "Failed to update practice schedule" });
    }
  });

  app.delete("/api/practice-schedules/:id", async (req, res) => {
    try {
      const deleted = await storage.deletePracticeSchedule(req.params.id);
      if (!deleted) {
        return res.status(404).json({ error: "Practice schedule not found" });
      }
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: "Failed to delete practice schedule" });
    }
  });

  // Practice History
  app.get("/api/practice-history/:userId", async (req, res) => {
    try {
      const { limit = 50, offset = 0 } = req.query;
      const history = await storage.getUserPracticeHistory(
        req.params.userId, 
        parseInt(limit as string), 
        parseInt(offset as string)
      );
      res.json(history);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch practice history" });
    }
  });

  app.post("/api/practice-history", async (req, res) => {
    try {
      const validatedData = insertPracticeHistorySchema.parse(req.body);
      const historyEntry = await storage.createPracticeHistoryEntry(validatedData);
      res.json(historyEntry);
    } catch (error) {
      res.status(400).json({ error: "Invalid practice history data" });
    }
  });

  app.get("/api/practice-history/:userId/stats", async (req, res) => {
    try {
      const { days = 30 } = req.query;
      const stats = await storage.getPracticeStats(req.params.userId, parseInt(days as string));
      res.json(stats);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch practice stats" });
    }
  });

  // Songs
  app.get("/api/songs", async (req, res) => {
    try {
      const { q, genre, difficulty, artist } = req.query;
      const query = typeof q === 'string' ? q : '';

      const filters = {
        genre: typeof genre === 'string' ? genre : undefined,
        difficulty: typeof difficulty === 'string' ? parseInt(difficulty) : undefined,
        artist: typeof artist === 'string' ? artist : undefined,
      };

      const songs = await storage.searchSongs(query, filters);
      res.json(songs);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch songs" });
    }
  });

  app.get("/api/songs/:id", async (req, res) => {
    try {
      const song = await storage.getSong(req.params.id);
      if (!song) {
        return res.status(404).json({ error: "Song not found" });
      }
      res.json(song);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch song" });
    }
  });

  app.post("/api/songs", async (req, res) => {
    try {
      const validatedData = insertSongSchema.parse(req.body);
      const song = await storage.createSong(validatedData);
      res.json(song);
    } catch (error) {
      res.status(400).json({ error: "Invalid song data" });
    }
  });

  app.post("/api/songs/from-spotify", async (req, res) => {
    try {
      const { spotifyTrack } = req.body;

      // Check if song already exists
      const existingSong = await storage.getSongBySpotifyId(spotifyTrack.id);
      if (existingSong) {
        return res.status(409).json({ error: "Song already exists in library", song: existingSong });
      }

      // Convert Spotify track to our song format
      let songData = convertSpotifyTrackToSong(spotifyTrack);

      // Try to get audio features from Spotify
      try {
        const audioFeatures = await getTrackAudioFeatures(spotifyTrack.id);
        songData = {
          ...songData,
          key: audioFeatures.key || null,
          tempo: audioFeatures.tempo || null,
          timeSignature: audioFeatures.timeSignature || null,
        };
      } catch (audioError) {
        console.warn('Could not fetch audio features:', audioError);
      }

      // Validate and save
      const validatedData = insertSongSchema.parse(songData);
      const song = await storage.createSong(validatedData);
      res.json(song);
    } catch (error) {
      console.error('Error adding song from Spotify:', error);
      res.status(400).json({ error: "Failed to add song from Spotify" });
    }
  });

  app.patch("/api/songs/:id", async (req, res) => {
    try {
      const updated = await storage.updateSong(req.params.id, req.body);
      if (!updated) {
        return res.status(404).json({ error: "Song not found" });
      }
      res.json(updated);
    } catch (error) {
      res.status(500).json({ error: "Failed to update song" });
    }
  });

  app.delete("/api/songs/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteSong(req.params.id);
      if (!deleted) {
        return res.status(404).json({ error: "Song not found" });
      }
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: "Failed to delete song" });
    }
  });

  // Song Collections
  app.get("/api/song-collections", async (req, res) => {
    try {
      const { userId } = req.query;
      const userIdStr = typeof userId === 'string' ? userId : undefined;
      const collections = await storage.getUserSongCollections(userIdStr);
      res.json(collections);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch song collections" });
    }
  });

  app.get("/api/song-collections/:id", async (req, res) => {
    try {
      const collection = await storage.getSongCollection(req.params.id);
      if (!collection) {
        return res.status(404).json({ error: "Collection not found" });
      }
      res.json(collection);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch song collection" });
    }
  });

  app.post("/api/song-collections", async (req, res) => {
    try {
      console.log("Received POST /api/song-collections with body:", JSON.stringify(req.body, null, 2));
      const validatedData = insertSongCollectionSchema.parse(req.body);
      console.log("Validation successful, creating collection:", JSON.stringify(validatedData, null, 2));
      const collection = await storage.createSongCollection(validatedData);
      console.log("Collection created successfully:", JSON.stringify(collection, null, 2));
      res.json(collection);
    } catch (error) {
      console.error("Error creating song collection:", error);
      if (error instanceof Error) {
        console.error("Error message:", error.message);
        console.error("Error stack:", error.stack);
      }
      res.status(400).json({ error: "Invalid song collection data" });
    }
  });

  app.patch("/api/song-collections/:id", async (req, res) => {
    try {
      const updated = await storage.updateSongCollection(req.params.id, req.body);
      if (!updated) {
        return res.status(404).json({ error: "Song collection not found" });
      }
      res.json(updated);
    } catch (error) {
      res.status(500).json({ error: "Failed to update song collection" });
    }
  });

  app.delete("/api/song-collections/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteSongCollection(req.params.id);
      if (!deleted) {
        return res.status(404).json({ error: "Song collection not found" });
      }
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: "Failed to delete song collection" });
    }
  });

  app.post("/api/song-collections/:id/songs", async (req, res) => {
    try {
      const { songId } = req.body;
      const added = await storage.addSongToCollection(req.params.id, songId);
      if (!added) {
        return res.status(404).json({ error: "Collection not found" });
      }
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: "Failed to add song to collection" });
    }
  });

  app.delete("/api/song-collections/:id/songs/:songId", async (req, res) => {
    try {
      const removed = await storage.removeSongFromCollection(req.params.id, req.params.songId);
      if (!removed) {
        return res.status(404).json({ error: "Collection not found" });
      }
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: "Failed to remove song from collection" });
    }
  });

  // Spotify Integration
  app.get("/api/spotify/search", async (req, res) => {
    try {
      const { q } = req.query;
      if (!q || typeof q !== 'string') {
        return res.status(400).json({ error: "Search query required" });
      }

      const spotify = await getUncachableSpotifyClient();
      const results = await spotify.search(q, ['track'], 'US', 20);
      res.json(results);
    } catch (error) {
      console.error('Spotify search error:', error);
      res.status(500).json({ error: "Failed to search Spotify" });
    }
  });

  // Song Practice Sessions
  app.get("/api/song-practice-sessions/:userId", async (req, res) => {
    try {
      const { songId } = req.query;
      const songIdStr = typeof songId === 'string' ? songId : undefined;
      const sessions = await storage.getUserSongPracticeSessions(req.params.userId, songIdStr);
      res.json(sessions);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch song practice sessions" });
    }
  });

  app.post("/api/song-practice-sessions", async (req, res) => {
    try {
      const validatedData = insertSongPracticeSessionSchema.parse(req.body);
      const session = await storage.createSongPracticeSession(validatedData);
      res.json(session);
    } catch (error) {
      res.status(400).json({ error: "Invalid song practice session data" });
    }
  });

  app.patch("/api/song-practice-sessions/:id", async (req, res) => {
    try {
      const updated = await storage.updateSongPracticeSession(req.params.id, req.body);
      if (!updated) {
        return res.status(404).json({ error: "Song practice session not found" });
      }
      res.json(updated);
    } catch (error) {
      res.status(500).json({ error: "Failed to update song practice session" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type PracticeSession,
  type InsertPracticeSession,
  type PracticeGoal,
  type InsertPracticeGoal,
  type ChordProgression,
  type InsertChordProgression,
  type PracticeSchedule,
  type InsertPracticeSchedule,
  type PracticeHistory,
  type InsertPracticeHistory,
  type Song,
  type InsertSong,
  type SongCollection,
  type InsertSongCollection,
  type SongPracticeSession,
  type InsertSongPracticeSession
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  createPracticeSession(session: InsertPracticeSession): Promise<PracticeSession>;
  getUserPracticeSessions(userId: string): Promise<PracticeSession[]>;
  updatePracticeSession(id: string, updates: Partial<PracticeSession>): Promise<PracticeSession | undefined>;

  createPracticeGoal(goal: InsertPracticeGoal): Promise<PracticeGoal>;
  getUserPracticeGoals(userId: string): Promise<PracticeGoal[]>;
  updatePracticeGoal(id: string, updates: Partial<PracticeGoal>): Promise<PracticeGoal | undefined>;

  createChordProgression(progression: InsertChordProgression): Promise<ChordProgression>;
  getUserChordProgressions(userId: string): Promise<ChordProgression[]>;
  deleteChordProgression(id: string): Promise<boolean>;

  createPracticeSchedule(schedule: InsertPracticeSchedule): Promise<PracticeSchedule>;
  getUserPracticeSchedules(userId: string): Promise<PracticeSchedule[]>;
  updatePracticeSchedule(id: string, updates: Partial<PracticeSchedule>): Promise<PracticeSchedule | undefined>;
  deletePracticeSchedule(id: string): Promise<boolean>;

  createPracticeHistoryEntry(history: InsertPracticeHistory): Promise<PracticeHistory>;
  getUserPracticeHistory(userId: string, limit?: number, offset?: number): Promise<PracticeHistory[]>;
  getPracticeStats(userId: string, days: number): Promise<any>;

  // Song management
  createSong(song: InsertSong): Promise<Song>;
  getSong(id: string): Promise<Song | undefined>;
  searchSongs(query: string, filters?: { genre?: string; difficulty?: number; artist?: string }): Promise<Song[]>;
  updateSong(id: string, updates: Partial<Song>): Promise<Song | undefined>;
  deleteSong(id: string): Promise<boolean>;
  getSongBySpotifyId(spotifyId: string): Promise<Song | undefined>;

  // Song collection management
  createSongCollection(collection: InsertSongCollection): Promise<SongCollection>;
  getSongCollection(id: string): Promise<SongCollection | undefined>;
  getUserSongCollections(userId?: string): Promise<SongCollection[]>;
  updateSongCollection(id: string, updates: Partial<SongCollection>): Promise<SongCollection | undefined>;
  deleteSongCollection(id: string): Promise<boolean>;
  addSongToCollection(collectionId: string, songId: string): Promise<boolean>;
  removeSongFromCollection(collectionId: string, songId: string): Promise<boolean>;

  // Song practice session management
  createSongPracticeSession(session: InsertSongPracticeSession): Promise<SongPracticeSession>;
  getUserSongPracticeSessions(userId: string, songId?: string): Promise<SongPracticeSession[]>;
  updateSongPracticeSession(id: string, updates: Partial<SongPracticeSession>): Promise<SongPracticeSession | undefined>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private practiceSessions: Map<string, PracticeSession>;
  private practiceGoals: Map<string, PracticeGoal>;
  private chordProgressions: Map<string, ChordProgression>;
  private practiceSchedules: Map<string, PracticeSchedule>;
  private practiceHistory: Map<string, PracticeHistory>;
  private songs: Map<string, Song>;
  private songCollections: Map<string, SongCollection>;
  private songPracticeSessions: Map<string, SongPracticeSession>;

  constructor() {
    this.users = new Map();
    this.practiceSessions = new Map();
    this.practiceGoals = new Map();
    this.chordProgressions = new Map();
    this.practiceSchedules = new Map();
    this.practiceHistory = new Map();
    this.songs = new Map();
    this.songCollections = new Map();
    this.songPracticeSessions = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async createPracticeSession(insertSession: InsertPracticeSession): Promise<PracticeSession> {
    const id = randomUUID();
    const session: PracticeSession = { 
      ...insertSession, 
      id,
      userId: insertSession.userId || null,
      endTime: insertSession.endTime || null,
      completed: insertSession.completed || false
    };
    this.practiceSessions.set(id, session);
    return session;
  }

  async getUserPracticeSessions(userId: string): Promise<PracticeSession[]> {
    return Array.from(this.practiceSessions.values()).filter(
      (session) => session.userId === userId,
    );
  }

  async updatePracticeSession(id: string, updates: Partial<PracticeSession>): Promise<PracticeSession | undefined> {
    const session = this.practiceSessions.get(id);
    if (!session) return undefined;

    const updated = { ...session, ...updates };
    this.practiceSessions.set(id, updated);
    return updated;
  }

  async createPracticeGoal(insertGoal: InsertPracticeGoal): Promise<PracticeGoal> {
    const id = randomUUID();
    const goal: PracticeGoal = { 
      ...insertGoal, 
      id, 
      createdAt: new Date(),
      userId: insertGoal.userId || null,
      description: insertGoal.description || null,
      progress: insertGoal.progress || 0,
      completed: insertGoal.completed || false,
      targetDate: insertGoal.targetDate || null
    };
    this.practiceGoals.set(id, goal);
    return goal;
  }

  async getUserPracticeGoals(userId: string): Promise<PracticeGoal[]> {
    return Array.from(this.practiceGoals.values()).filter(
      (goal) => goal.userId === userId,
    );
  }

  async updatePracticeGoal(id: string, updates: Partial<PracticeGoal>): Promise<PracticeGoal | undefined> {
    const goal = this.practiceGoals.get(id);
    if (!goal) return undefined;

    const updated = { ...goal, ...updates };
    this.practiceGoals.set(id, updated);
    return updated;
  }

  async createChordProgression(insertProgression: InsertChordProgression): Promise<ChordProgression> {
    const id = randomUUID();
    const progression: ChordProgression = { 
      ...insertProgression, 
      id, 
      createdAt: new Date(),
      userId: insertProgression.userId || null
    };
    this.chordProgressions.set(id, progression);
    return progression;
  }

  async getUserChordProgressions(userId: string): Promise<ChordProgression[]> {
    return Array.from(this.chordProgressions.values()).filter(
      (progression) => progression.userId === userId,
    );
  }

  async deleteChordProgression(id: string): Promise<boolean> {
    return this.chordProgressions.delete(id);
  }

  async createPracticeSchedule(insertSchedule: InsertPracticeSchedule): Promise<PracticeSchedule> {
    const id = randomUUID();
    const schedule: PracticeSchedule = { 
      ...insertSchedule, 
      id, 
      createdAt: new Date(),
      userId: insertSchedule.userId || null,
      isActive: insertSchedule.isActive ?? true
    };
    this.practiceSchedules.set(id, schedule);
    return schedule;
  }

  async getUserPracticeSchedules(userId: string): Promise<PracticeSchedule[]> {
    return Array.from(this.practiceSchedules.values()).filter(
      (schedule) => schedule.userId === userId,
    );
  }

  async updatePracticeSchedule(id: string, updates: Partial<PracticeSchedule>): Promise<PracticeSchedule | undefined> {
    const schedule = this.practiceSchedules.get(id);
    if (!schedule) return undefined;

    const updated = { ...schedule, ...updates };
    this.practiceSchedules.set(id, updated);
    return updated;
  }

  async deletePracticeSchedule(id: string): Promise<boolean> {
    return this.practiceSchedules.delete(id);
  }

  async createPracticeHistoryEntry(insertHistory: InsertPracticeHistory): Promise<PracticeHistory> {
    const id = randomUUID();
    const history: PracticeHistory = { 
      ...insertHistory, 
      id, 
      createdAt: new Date(),
      userId: insertHistory.userId || null,
      notes: insertHistory.notes || null
    };
    this.practiceHistory.set(id, history);
    return history;
  }

  async getUserPracticeHistory(userId: string, limit: number = 50, offset: number = 0): Promise<PracticeHistory[]> {
    const userHistory = Array.from(this.practiceHistory.values())
      .filter((history) => history.userId === userId)
      .sort((a, b) => new Date(b.practiceDate).getTime() - new Date(a.practiceDate).getTime());

    return userHistory.slice(offset, offset + limit);
  }

  async getPracticeStats(userId: string, days: number): Promise<any> {
    const cutoffDate = new Date();
    cutoffDate.setDate(cutoffDate.getDate() - days);

    const userHistory = Array.from(this.practiceHistory.values())
      .filter((history) => 
        history.userId === userId && 
        new Date(history.practiceDate) >= cutoffDate
      );

    const totalSessions = userHistory.length;
    const totalMinutes = userHistory.reduce((sum, h) => sum + h.totalDuration, 0);
    const averageCompletionRate = totalSessions > 0 
      ? userHistory.reduce((sum, h) => sum + (h.completedExercises / h.totalExercises), 0) / totalSessions
      : 0;

    return {
      totalSessions,
      totalMinutes,
      averageCompletionRate: Math.round(averageCompletionRate * 100),
      averageSessionLength: totalSessions > 0 ? Math.round(totalMinutes / totalSessions) : 0
    };
  }

  // Song management methods
  async createSong(insertSong: InsertSong): Promise<Song> {
    const id = randomUUID();
    const song: Song = {
      ...insertSong,
      id,
      createdAt: new Date(),
      updatedAt: new Date(),
      album: insertSong.album || null,
      genre: insertSong.genre || null,
      key: insertSong.key || null,
      tempo: insertSong.tempo || null,
      duration: insertSong.duration || null,
      spotifyId: insertSong.spotifyId || null,
      chordProgression: insertSong.chordProgression || null,
      lyrics: insertSong.lyrics || null,
      tabs: insertSong.tabs || null,
      notes: insertSong.notes || null,
      timeSignature: insertSong.timeSignature || "4/4",
    };
    this.songs.set(id, song);
    return song;
  }

  async getSong(id: string): Promise<Song | undefined> {
    return this.songs.get(id);
  }

  async searchSongs(query: string, filters?: { genre?: string; difficulty?: number; artist?: string }): Promise<Song[]> {
    const allSongs = Array.from(this.songs.values());
    const lowerQuery = query.toLowerCase();

    return allSongs.filter(song => {
      // Text search
      const matchesQuery = !query || 
        song.title.toLowerCase().includes(lowerQuery) ||
        song.artist.toLowerCase().includes(lowerQuery) ||
        (song.album && song.album.toLowerCase().includes(lowerQuery));

      // Filter by genre
      const matchesGenre = !filters?.genre || song.genre === filters.genre;

      // Filter by difficulty
      const matchesDifficulty = !filters?.difficulty || song.difficulty === filters.difficulty;

      // Filter by artist
      const matchesArtist = !filters?.artist || song.artist.toLowerCase().includes(filters.artist.toLowerCase());

      return matchesQuery && matchesGenre && matchesDifficulty && matchesArtist;
    });
  }

  async updateSong(id: string, updates: Partial<Song>): Promise<Song | undefined> {
    const song = this.songs.get(id);
    if (!song) return undefined;

    const updated = { ...song, ...updates, updatedAt: new Date() };
    this.songs.set(id, updated);
    return updated;
  }

  async deleteSong(id: string): Promise<boolean> {
    return this.songs.delete(id);
  }

  async getSongBySpotifyId(spotifyId: string): Promise<Song | undefined> {
    return Array.from(this.songs.values()).find(song => song.spotifyId === spotifyId);
  }

  // Song collection management methods
  async createSongCollection(insertCollection: InsertSongCollection): Promise<SongCollection> {
    const id = randomUUID();
    const collection: SongCollection = {
      ...insertCollection,
      id,
      createdAt: new Date(),
      updatedAt: new Date(),
      description: insertCollection.description || null,
      category: insertCollection.category || null,
      userId: insertCollection.userId || null,
      isPublic: insertCollection.isPublic || false,
    };
    this.songCollections.set(id, collection);
    return collection;
  }

  async getSongCollection(id: string): Promise<SongCollection | undefined> {
    return this.songCollections.get(id);
  }

  async getUserSongCollections(userId?: string): Promise<SongCollection[]> {
    return Array.from(this.songCollections.values()).filter(collection =>
      userId ? collection.userId === userId || collection.isPublic : collection.isPublic
    );
  }

  async updateSongCollection(id: string, updates: Partial<SongCollection>): Promise<SongCollection | undefined> {
    const collection = this.songCollections.get(id);
    if (!collection) return undefined;

    const updated = { ...collection, ...updates, updatedAt: new Date() };
    this.songCollections.set(id, updated);
    return updated;
  }

  async deleteSongCollection(id: string): Promise<boolean> {
    return this.songCollections.delete(id);
  }

  async addSongToCollection(collectionId: string, songId: string): Promise<boolean> {
    const collection = this.songCollections.get(collectionId);
    if (!collection) return false;

    const songIds = Array.isArray(collection.songIds) ? collection.songIds : [];
    if (!songIds.includes(songId)) {
      songIds.push(songId);
      await this.updateSongCollection(collectionId, { songIds });
    }
    return true;
  }

  async removeSongFromCollection(collectionId: string, songId: string): Promise<boolean> {
    const collection = this.songCollections.get(collectionId);
    if (!collection) return false;

    const songIds = Array.isArray(collection.songIds) ? collection.songIds : [];
    const filteredIds = songIds.filter(id => id !== songId);
    await this.updateSongCollection(collectionId, { songIds: filteredIds });
    return true;
  }

  // Song practice session management methods
  async createSongPracticeSession(insertSession: InsertSongPracticeSession): Promise<SongPracticeSession> {
    const id = randomUUID();
    const session: SongPracticeSession = {
      ...insertSession,
      id,
      createdAt: new Date(),
      tempo: insertSession.tempo || null,
      sections: insertSession.sections || null,
      notes: insertSession.notes || null,
      rating: insertSession.rating || null,
      completed: insertSession.completed || true,
    };
    this.songPracticeSessions.set(id, session);
    return session;
  }

  async getUserSongPracticeSessions(userId: string, songId?: string): Promise<SongPracticeSession[]> {
    return Array.from(this.songPracticeSessions.values()).filter(session =>
      session.userId === userId && (!songId || session.songId === songId)
    );
  }

  async updateSongPracticeSession(id: string, updates: Partial<SongPracticeSession>): Promise<SongPracticeSession | undefined> {
    const session = this.songPracticeSessions.get(id);
    if (!session) return undefined;

    const updated = { ...session, ...updates };
    this.songPracticeSessions.set(id, updated);
    return updated;
  }
}

export const storage = new MemStorage();

