  --border: hsl(217, 32%, 17.5%);
  --input: hsl(217, 32%, 17.5%);
  --ring: hsl(263, 70%, 50.4%);
  --chart-1: hsl(263, 70%, 50.4%);
  --chart-2: hsl(173, 58%, 39%);
  --chart-3: hsl(197, 37%, 24%);
  --chart-4: hsl(43, 74%, 66%);
  --chart-5: hsl(27, 87%, 67%);
  --radius: 0.5rem;
  --guitar-purple: hsl(263, 70%, 50.4%);
  --guitar-amber: hsl(43, 91%, 48%);
  --dark-slate: hsl(222, 84%, 4.9%);
  --dark-panel: hsl(217, 32%, 17.5%);
  --font-sans: 'Inter', system-ui, sans-serif;
}

.dark {
  --background: hsl(222, 84%, 4.9%);
  --foreground: hsl(210, 40%, 98%);
  --card: hsl(222, 84%, 4.9%);
  --card-foreground: hsl(210, 40%, 98%);
  --popover: hsl(222, 84%, 4.9%);
  --popover-foreground: hsl(210, 40%, 98%);
  --primary: hsl(263, 70%, 50.4%);
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: hsl(217, 32%, 17.5%);
  --secondary-foreground: hsl(210, 40%, 98%);
  --muted: hsl(217, 32%, 17.5%);
  --muted-foreground: hsl(215, 20.2%, 65.1%);
  --accent: hsl(217, 32%, 17.5%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --border: hsl(217, 32%, 17.5%);
  --input: hsl(217, 32%, 17.5%);
  --ring: hsl(263, 70%, 50.4%);
  --chart-1: hsl(263, 70%, 50.4%);
  --chart-2: hsl(173, 58%, 39%);
  --chart-3: hsl(197, 37%, 24%);
  --chart-4: hsl(43, 74%, 66%);
  --chart-5: hsl(27, 87%, 67%);
  --guitar-purple: hsl(263, 70%, 50.4%);
  --guitar-amber: hsl(43, 91%, 48%);
  --dark-slate: hsl(222, 84%, 4.9%);
  --dark-panel: hsl(217, 32%, 17.5%);
  --font-sans: 'Inter', system-ui, sans-serif;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: var(--font-sans);
    background-color: var(--dark-slate);
    color: hsl(210, 40%, 98%);
  }
}

/* Custom slider styles */
.slider::-webkit-slider-thumb {
  appearance: none;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background: var(--guitar-purple);
  cursor: pointer;
  border: 2px solid #fff;
  box-shadow: 0 2px 6px rgba(0,0,0,0.2);
}

.slider::-moz-range-thumb {
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background: var(--guitar-purple);
  cursor: pointer;
  border: 2px solid #fff;
  box-shadow: 0 2px 6px rgba(0,0,0,0.2);
}

.slider::-webkit-slider-track {
  height: 8px;
  border-radius: 4px;
  background: hsl(215, 25%, 27%);
}

.slider::-moz-range-track {
  height: 8px;
  border-radius: 4px;
  background: hsl(215, 25%, 27%);
}

/* Fretboard styles */
.fret-string {
  border-bottom: 2px solid #92400e;
}

.fret-note {
  @apply w-4 h-4 rounded-full absolute;
  top: -8px;
  left: 50%;
  transform: translateX(-50%);
}

.fret-note.root {
  background-color: var(--guitar-amber);
}

.fret-note.scale {
  background-color: var(--guitar-purple);
}

/* Metronome pulse animation */
@keyframes pulse {
  0% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.05);
    opacity: 0.8;
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}

.metronome-pulse {
  animation: pulse 0.1s ease-in-out;
}

/* Tab content transitions */
.tab-content {
  transition: opacity 0.2s ease-in-out;
}

