import { useState } from 'react';
import { Link, useLocation } from 'wouter';
import { Button } from '@/components/ui/button';
import { 
  Clock, 
  Hand, 
  Music, 
  Radio, 
  ClipboardList, 
  Calendar,
  History,
  Menu,
  Guitar,
  Library
} from 'lucide-react';

interface HeaderProps {
  activeTab: string;
  onTabChange: (tab: string) => void;
}

const tabs = [
  { id: 'metronome', label: 'Metronome', icon: Clock },
  { id: 'chords', label: 'Chords', icon: Hand },
  { id: 'scales', label: 'Scales', icon: Music },
  { id: 'tuner', label: 'Tuner', icon: Radio },
  { id: 'practice', label: 'Practice', icon: ClipboardList },
  { id: 'schedule', label: 'Schedule', icon: Calendar },
  { id: 'history', label: 'History', icon: History }
];

const navigationLinks = [
  { href: '/', label: 'Practice Tools', icon: Guitar, id: 'home' },
  { href: '/songs', label: 'Song Library', icon: Library, id: 'songs' }
];

export default function Header({ activeTab, onTabChange }: HeaderProps) {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [location] = useLocation();

  return (
    <header className="bg-dark-panel border-b border-slate-700 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <Link href="/">
            <div className="flex items-center space-x-3 cursor-pointer">
              <div className="w-8 h-8 bg-gradient-to-r from-[#6366f1] to-[#f59e0b] rounded-lg flex items-center justify-center">
                <Guitar className="text-white text-lg" size={20} />
              </div>
              <h1 className="text-xl font-bold text-white">Guitar Practice Pro</h1>
            </div>
          </Link>

          <nav className="hidden md:flex space-x-1">
            {/* Navigation Links */}
            {navigationLinks.map((link) => {
              const Icon = link.icon;
              const isActive = location === link.href;
              return (
                <Link key={link.id} href={link.href}>
                  <Button
                    variant={isActive ? "default" : "ghost"}
                    className={`px-4 py-2 font-medium transition-colors ${
                      isActive
                        ? 'bg-[#6366f1] text-white hover:bg-[#6366f1]/80'
                        : 'text-slate-300 hover:text-white hover:bg-slate-700'
                    }`}
                  >
                    <Icon className="mr-2" size={16} />
                    {link.label}
                  </Button>
                </Link>
              );
            })}

            {/* Show internal tabs only on home page */}
            {location === '/' && tabs.map((tab) => {
              const Icon = tab.icon;
              return (
                <Button
                  key={tab.id}
                  variant={activeTab === tab.id ? "default" : "ghost"}
                  className={`px-4 py-2 font-medium transition-colors ${
                    activeTab === tab.id
                      ? 'bg-[#6366f1] text-white hover:bg-[#6366f1]/80'
                      : 'text-slate-300 hover:text-white hover:bg-slate-700'
                  }`}
                  onClick={() => onTabChange(tab.id)}
                >
                  <Icon className="mr-2" size={16} />
                  {tab.label}
                </Button>
              );
            })}
          </nav>

          <Button
            variant="ghost"
            size="sm"
            className="md:hidden p-2 text-slate-300 hover:text-white hover:bg-slate-700"
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
          >
            <Menu size={20} />
          </Button>
        </div>
      </div>

      {/* Mobile Navigation */}
      {mobileMenuOpen && (
        <div className="md:hidden bg-dark-panel border-t border-slate-700">
          <div className="px-4 py-2 space-y-1">
            {/* Navigation Links */}
            {navigationLinks.map((link) => {
              const Icon = link.icon;
              const isActive = location === link.href;
              return (
                <Link key={link.id} href={link.href}>
                  <Button
                    variant={isActive ? "default" : "ghost"}
                    className={`w-full justify-start px-4 py-3 font-medium transition-colors ${
                      isActive
                        ? 'bg-[#6366f1] text-white hover:bg-[#6366f1]/80'
                        : 'text-slate-300 hover:text-white hover:bg-slate-700'
                    }`}
                    onClick={() => setMobileMenuOpen(false)}
                  >
                    <Icon className="mr-2" size={16} />
                    {link.label}
                  </Button>
                </Link>
              );
            })}

            {/* Show internal tabs only on home page */}
            {location === '/' && tabs.map((tab) => {
              const Icon = tab.icon;
              return (
                <Button
                  key={tab.id}
                  variant={activeTab === tab.id ? "default" : "ghost"}
                  className={`w-full justify-start px-4 py-3 font-medium transition-colors ${
                    activeTab === tab.id
                      ? 'bg-[#6366f1] text-white hover:bg-[#6366f1]/80'
                      : 'text-slate-300 hover:text-white hover:bg-slate-700'
                  }`}
                  onClick={() => {
                    onTabChange(tab.id);
                    setMobileMenuOpen(false);
                  }}
                >
                  <Icon className="mr-2" size={16} />
                  {tab.label}
                </Button>
              );
            })}
          </div>
        </div>
      )}
    </header>
  );
}

client/src/components/metronome.tsx.content:
import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Slider } from '@/components/ui/slider';
import { Switch } from '@/components/ui/switch';
import { Clock, Play, Pause, Square, Minus, Plus } from 'lucide-react';
import { useMetronome } from '@/hooks/use-metronome';

export default function Metronome() {
  const {
    bpm,
    isPlaying,
    timeSignature,
    currentBeat,
    volume,
    accentEnabled,
    start,
    stop,
    toggle,
    setBpm,
    setTimeSignature,
    setVolume,
    setAccentEnabled
  } = useMetronome();

  const [practiceTime, setPracticeTime] = useState(0);
  const [practiceTimer, setPracticeTimer] = useState<NodeJS.Timeout | null>(null);
  const [pulseClass, setPulseClass] = useState('');

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const startPracticeTimer = () => {
    if (practiceTimer) return;

    const timer = setInterval(() => {
      setPracticeTime(prev => prev + 1);
    }, 1000);

    setPracticeTimer(timer);
  };

  const resetPracticeTimer = () => {
    if (practiceTimer) {
      clearInterval(practiceTimer);
      setPracticeTimer(null);
    }
    setPracticeTime(0);
  };

  const timeSignatures: [number, number][] = [
    [4, 4], [3, 4], [2, 4], [6, 8]
  ];

  // Add visual pulse effect when metronome plays
  useEffect(() => {
    if (isPlaying) {
      setPulseClass('metronome-pulse');
      const timeout = setTimeout(() => setPulseClass(''), 100);
      return () => clearTimeout(timeout);
    }
  }, [currentBeat, isPlaying]);

  useEffect(() => {
    return () => {
      if (practiceTimer) {
        clearInterval(practiceTimer);
      }
    };
  }, [practiceTimer]);

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
      {/* Metronome Control */}
      <div className="lg:col-span-2">
        <Card className="bg-dark-panel border-slate-700">
          <CardContent className="p-8">
            <h2 className="text-2xl font-bold text-white mb-6 flex items-center">
              <Clock className="mr-3 text-[#6366f1]" size={28} />
              Metronome
            </h2>

            {/* BPM Display */}
            <div className="text-center mb-8">
              <div className={`inline-flex items-center justify-center w-32 h-32 rounded-full border-4 border-[#6366f1] bg-gradient-to-r from-[#6366f1]/20 to-[#f59e0b]/20 mb-4 ${pulseClass}`}>
                <div className="text-center">
                  <div className="text-4xl font-bold text-white">{bpm}</div>
                  <div className="text-sm text-slate-400">BPM</div>
                </div>
              </div>
            </div>

            {/* BPM Controls */}
            <div className="flex items-center justify-center space-x-4 mb-8">
              <Button
                size="sm"
                variant="secondary"
                className="w-12 h-12 rounded-full bg-slate-700 hover:bg-slate-600"
                onClick={() => setBpm(bpm - 1)}
              >
                <Minus size={16} />
              </Button>
              <div className="flex-1 max-w-md">
                <Slider
                  value={[bpm]}
                  onValueChange={([value]) => setBpm(value)}
                  min={60}
                  max={200}
                  step={1}
                  className="slider"
                />
              </div>
              <Button
                size="sm"
                variant="secondary"
                className="w-12 h-12 rounded-full bg-slate-700 hover:bg-slate-600"
                onClick={() => setBpm(bpm + 1)}
              >
                <Plus size={16} />
              </Button>
            </div>

            {/* Play/Pause Controls */}
            <div className="flex items-center justify-center space-x-4">
              <Button
                size="lg"
                className="bg-[#6366f1] hover:bg-[#6366f1]/80 text-white px-8 py-4 font-semibold text-lg"
                onClick={toggle}
              >
                {isPlaying ? (
                  <>
                    <Pause className="mr-3" size={20} />
                    Pause
                  </>
                ) : (
                  <>
                    <Play className="mr-3" size={20} />
                    Start
                  </>
                )}
              </Button>
              <Button
                variant="secondary"
                size="lg"
                className="bg-slate-700 hover:bg-slate-600 text-white px-6 py-4 font-semibold"
                onClick={stop}
              >
                <Square size={16} />
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Settings & Timer */}
      <div className="space-y-6">
        {/* Time Signature */}
        <Card className="bg-dark-panel border-slate-700">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold text-white mb-4">Time Signature</h3>
            <div className="grid grid-cols-2 gap-2">
              {timeSignatures.map((sig) => (
                <Button
                  key={`${sig[0]}/${sig[1]}`}
                  variant={timeSignature[0] === sig[0] && timeSignature[1] === sig[1] ? "default" : "secondary"}
                  className={`p-3 font-semibold transition-colors ${
                    timeSignature[0] === sig[0] && timeSignature[1] === sig[1]
                      ? 'bg-[#6366f1] text-white'
                      : 'bg-slate-700 hover:bg-slate-600 text-slate-300 hover:text-white'
                  }`}
                  onClick={() => setTimeSignature(sig)}
                >
                  {sig[0]}/{sig[1]}
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Sound Settings */}
        <Card className="bg-dark-panel border-slate-700">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold text-white mb-4">Sound</h3>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <span className="text-slate-300">Volume</span>
                <div className="w-24">
                  <Slider
                    value={[volume * 100]}
                    onValueChange={([value]) => setVolume(value / 100)}
                    min={0}
                    max={100}
                    step={1}
                    className="slider"
                  />
                </div>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-slate-300">Accent Beat</span>
                <Switch
                  checked={accentEnabled}
                  onCheckedChange={setAccentEnabled}
                />
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Practice Timer */}
        <Card className="bg-dark-panel border-slate-700">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold text-white mb-4">Practice Timer</h3>
            <div className="text-center">
              <div className="text-2xl font-mono text-[#f59e0b] mb-3">
                {formatTime(practiceTime)}
              </div>
              <div className="flex space-x-2">
                <Button
                  size="sm"
                  className="flex-1 bg-green-600 hover:bg-green-500 text-white"
                  onClick={startPracticeTimer}
                  disabled={!!practiceTimer}
                >
                  <Play className="mr-1" size={14} />
                  Start
                </Button>
                <Button
                  size="sm"
                  variant="secondary"
                  className="flex-1 bg-slate-700 hover:bg-slate-600 text-white"
                  onClick={resetPracticeTimer}
                >
                  Reset
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Beat Indicator */}
        <Card className="bg-dark-panel border-slate-700">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold text-white mb-4">Current Beat</h3>
            <div className="flex justify-center space-x-2">
              {Array.from({ length: timeSignature[0] }, (_, i) => (
                <div
                  key={i}
                  className={`w-8 h-8 rounded-full border-2 flex items-center justify-center text-sm font-semibold transition-colors ${
                    currentBeat === i + 1
                      ? 'border-[#6366f1] bg-[#6366f1] text-white'
                      : 'border-slate-600 text-slate-400'
                  }`}
                >
                  {i + 1}
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

