              {[
                { title: 'Morning Practice', type: 'Chord changes', duration: '25 min', time: 'Today, 9:30 AM' },
                { title: 'Scale Practice', type: 'C Major patterns', duration: '20 min', time: 'Yesterday, 7:15 PM' },
